    }
  };

  return (
    <main className="flex flex-1 flex-col items-center justify-center gap-4 p-4 sm:px-6 sm:py-0 md:gap-8">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle className="text-2xl font-bold text-center">Sign Up</CardTitle>
          <CardDescription className="text-center">Create a new account to get started.</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {error && <div className="text-red-500">{error}</div>}
          <div>
            <label className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">Email</label>
            <Input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">Password</label>
            <Input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </CardContent>
        <CardFooter className="flex justify-center">
          <Button onClick={handleSignup} disabled={loading}>
            {loading ? 'Signing up...' : 'Sign Up'}
          </Button>
        </CardFooter>
      </Card>
    </main>
  );
}
```

4.  **Create Login/Signup route:** In nextjs, add login and signup routes: create `src/app/login/page.tsx` and `src/app/signup/page.tsx` and add content from above

5.  **Add Logout Logic:** Update the `LogoutPage` to use Supabase for signing out.

```typescript
// src/app/logout/page.tsx
'use client';

import { useRouter } from 'next/navigation';
import { supabase } from '@/lib/supabaseClient';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from '@/components/ui/card';
import { LogOut } from 'lucide-react';
import Link from 'next/link';

export default function LogoutPage() {
  const router = useRouter();

  const handleLogout = async () => {
    try {
      const { error } = await supabase.auth.signOut();
      if (error) {
        console.error('Logout error:', error.message);
        // Handle logout error (e.g., display an error message)
      } else {
        // Redirect to login page after successful logout
        router.push('/login');
      }
    } catch (error) {
      console.error('Unexpected logout error:', error);
    }
  };

  return (
    <main className="flex flex-1 flex-col items-center justify-center gap-4 p-4 sm:px-6 sm:py-0 md:gap-8">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 justify-center">
            <LogOut className="h-6 w-6" /> Logout
          </CardTitle>
          <CardDescription className="text-center">Are you sure you want to log out?</CardDescription>
        </CardHeader>
        <CardContent className="flex justify-center">
          <Button onClick={handleLogout} variant="destructive">
            Confirm Logout
          </Button>
        </CardContent>
        <CardFooter className="text-center text-sm text-muted-foreground justify-center">
          <Link href="/">Or go back to Dashboard</Link>
        </CardFooter>
      </Card>
    </main>
  );
}
```

6.  **Protect Routes:** Create a middleware to check authentication and redirect unauthenticated users.
    * Install middleware:
    ```bash
    mkdir src/middleware
    ```
    * Create file src/middleware.ts with the following content:

```typescript
// src/middleware.ts
import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';

import { createClient } from '@/lib/supabaseClient';

export async function middleware(req: NextRequest) {
  const res = NextResponse.next();
  const supabase = createClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
  );

  const {
    data: { session },
  } = await supabase.auth.getSession();

  // Protected routes
  const protectedRoutes = [
    '/',
    '/work-orders',
    '/customers',
    '/locations',
    '/equipment',
    '/deficiencies',
    '/repairs',
    '/inventory',
    '/purchase-orders',
    '/scheduling',
    '/estimates',
    '/invoicing',
    '/checklists',
    '/gps-tracking',
    '/files',
    '/reports',
    '/portal',
    '/chat',
    '/automation',
    '/roles',
    '/audit-logs',
    '/platform/companies',
    '/platform/templates',
    '/language',
    '/settings',
    '/support',
  ];

  if (protectedRoutes.includes(req.nextUrl.pathname)) {
    if (!session) {
      const url = new URL(`/login`, req.url);
      return NextResponse.redirect(url);
    }
  }

  return res;
}

// See "Matching Paths" below to learn more
export const config = {
  matcher: [
    /*
     * Match all request paths except for the ones starting with:
     * - api (API routes)
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     */
    '/((?!api|_next/static|_next/image|favicon.ico).*)',
  ],
};
```

