```typescript
// src/lib/firebase/adminAuth.ts
import { type NextRequest } from 'next/server';
import { authAdmin, dbAdmin } from '@/lib/firebase/adminConfig';
import { COLLECTIONS } from '@/lib/firebase/collections';
import type { Role, ModulePermissions } from '@/types/role';
import { initialRoleTemplates } from '@/lib/roles-data';

interface AuthVerificationResult {
  authorized: boolean;
  userId?: string;
  companyId?: string | null;
  error?: string;
  status?: number;
}

export async function verifyUserRole(
  request: NextRequest,
  requiredPermission: string,
  targetCompanyId?: string | null, // Explicitly company context for resource
  targetUserId?: string // Optional: if permission is about a specific user
): Promise<AuthVerificationResult> {
  const authorization = request.headers.get('Authorization');
  if (!authorization?.startsWith('Bearer ')) {
    return { authorized: false, error: 'Unauthorized: Missing or invalid Bearer token', status: 401 };
  }

  const idToken = authorization.split('Bearer ')[1];
  if (!idToken) {
    return { authorized: false, error: 'Unauthorized: Invalid token format', status: 401 };
  }

  try {
    const decodedToken = await authAdmin.verifyIdToken(idToken);
    const userId = decodedToken.uid;

    const userProfileRef = dbAdmin.collection(COLLECTIONS.USERS).doc(userId);
    const userProfileSnap = await userProfileRef.get();

    if (!userProfileSnap.exists) {
      console.error(`[verifyUserRole] User profile not found for UID: ${userId}`);
      return { authorized: false, error: 'Forbidden: User profile not found', status: 403 };
    }

    const userProfileData = userProfileSnap.data()!;
    const userCompanyId = userProfileData.company_id ?? null; // User's actual company_id

    console.log(`[verifyUserRole] Verifying user ${userId} (Company: ${userCompanyId}) for perm [${requiredPermission}] on targetCompany: ${targetCompanyId === undefined ? 'Not Specified' : targetCompanyId}`);

    const isPlatformAdminAction = requiredPermission.startsWith('platform-');
    const isUserPlatformAdmin = userCompanyId === null;

    // Context Check:
    // 1. If it's a platform admin action, the user MUST be a platform admin.
    if (isPlatformAdminAction && !isUserPlatformAdmin) {
      console.warn(`[verifyUserRole] Denied: Non-platform user ${userId} trying platform action [${requiredPermission}].`);
      return { authorized: false, error: 'Forbidden: Platform administrative privileges required', status: 403 };
    }
    // 2. If it's NOT a platform admin action, and a targetCompanyId is specified, the user's companyId must match the targetCompanyId.
    //    (Unless the user is a platform admin, who can act on any company for non-platform actions if their role allows).
    if (!isPlatformAdminAction && targetCompanyId !== undefined && !isUserPlatformAdmin && userCompanyId !== targetCompanyId) {
      console.warn(`[verifyUserRole] Denied: User ${userId} (Company ${userCompanyId}) trying action [${requiredPermission}] on different company ${targetCompanyId}.`);
      return { authorized: false, error: 'Forbidden: Access denied to this resource', status: 403 };
    }
    // 3. If targetCompanyId is for a specific company resource, but user is platform admin, this is generally okay.
    //    The permission check below will determine if their platform role allows the action.

    // Fetch Role Links based on the user's actual company context
    const rolesLinkRef = dbAdmin.collection(COLLECTIONS.USER_ROLES);
    const rolesQuery = await rolesLinkRef
      .where('user_id', '==', userId)
      .where('company_id', '==', userCompanyId) // Use user's actual company_id (null for platform admins)
      .get();

    if (rolesQuery.empty) {
      console.warn(`[verifyUserRole] No role links found for user ${userId} in their context (Company: ${userCompanyId}).`);
      return { authorized: false, error: 'Forbidden: User has no assigned roles', status: 403 };
    }
    const userRoleIds = rolesQuery.docs.map(doc => doc.data().role_id as string);

    // Fetch Role Definitions
    // These roles will either be platform roles (company_id: null) or company-specific roles.
    let userRoles: Role[] = [];
    if (userRoleIds.length > 0) {
        const rolesSnapshot = await dbAdmin.collection(COLLECTIONS.ROLES)
            .where(admin.firestore.FieldPath.documentId(), 'in', userRoleIds)
            // Ensure the fetched roles match the user's company context (or are platform roles if user is platform admin)
            .where('company_id', '==', userCompanyId)
            .get();
        userRoles = rolesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as Role));
    }

    if (userRoles.length === 0) {
        console.warn(`[verifyUserRole] No role definitions found for role IDs [${userRoleIds.join(', ')}] in context (Company: ${userCompanyId}).`);
        return { authorized: false, error: 'Forbidden: Role definitions not found', status: 403 };
    }

    const [moduleSlug, action = 'can_access'] = requiredPermission.split(':');
    let hasPerm = false;

    for (const role of userRoles) {
      // Platform Owner special case: full access to everything
      if (role.id === 'platform-owner' && isUserPlatformAdmin) {
        hasPerm = true;
        console.log(`[verifyUserRole] Granted via Platform Owner role for [${requiredPermission}].`);
        break;
      }

      const permissionsForModule = role.permissions?.[moduleSlug];
      if (permissionsForModule) {
        if (typeof permissionsForModule === 'object') {
          if (permissionsForModule[action as keyof ModulePermissions] === true || permissionsForModule.manage === true) {
            hasPerm = true;
            console.log(`[verifyUserRole] Granted via Role: ${role.name} for [${requiredPermission}].`);
            break;
          }
        } else if (permissionsForModule === true) {
          hasPerm = true;
          console.log(`[verifyUserRole] Granted via Role (full module access): ${role.name} for [${requiredPermission}].`);
          break;
        }
      }
    }

    if (!hasPerm) {
      console.warn(`[verifyUserRole] Denied: User ${userId} (Roles: ${userRoles.map(r=>r.name).join(', ')}) for action [${requiredPermission}] in context (Company: ${userCompanyId}).`);
      return { authorized: false, error: `Forbidden: Missing required permission (${requiredPermission})`, status: 403 };
    }

    console.log(`[verifyUserRole] Authorized: User ${userId} (Company: ${userCompanyId}) for [${requiredPermission}].`);
    return { authorized: true, userId, companyId: userCompanyId };

  } catch (error: any) {
    console.error('[verifyUserRole] Internal error:', error);
    if (error.code === 'auth/id-token-expired') {
      return { authorized: false, error: 'Unauthorized: Token expired', status: 401 };
    }
    if (error.code === 'auth/argument-error') {
       return { authorized: false, error: 'Unauthorized: Invalid token format', status: 401 };
    }
    return { authorized: false, error: 'Internal Server Error during authentication', status: 500 };
  }
}
```

